mapper.select:
select t.id,t.task_theme as taskTheme,t.task_text as taskText,t.user_name as userName,t.task_day as taskDay,t.task_date as taskDate,
t.implement_date as implementDate,t.success_date as successDate,t.recipient,t.implement,t.speed,t.state
from tasks t limit #{startPage},#{rows}

select t.id,t.task_theme as taskTheme,t.task_text as taskText,t.user_name as userName,t.task_day as taskDay,t.task_date as taskDate,
t.implement_date as implementDate,t.success_date as successDate,t.recipient,t.implement,t.speed,t.state
 from tasks t where t.implement=#{userName} or t.recipient=#{userName} LIMIT #{startpage},#{rows}
